import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
    work: {
        color: '#faf7f7',
        backgroundColor: '#FF6C00',
        width: 300,
        height: 400,
        fontSize: 40,
        marginBottom: 0,
        padding: '0.5rem calc((100vw - 900px) / 2)'
    },
    }));

export default useStyles;
