import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
    font: {
        flexGrow: 1,
        display: 'flex',
        alignItems: 'center',
        color: '#000000',
        fontFamily: 'liebedoni-solid',
        fontSize: 40,
        ['@media (max-width:900px)']: { // eslint-disable-line no-useless-computed-key
             fontSize: 30,
        }
    },
    text: {
        flexGrow: 1,
        display: 'flex',
        alignItems: 'center',
        color: '#000000',
        fontSize: 20,
        ['@media (max-width:900px)']: { // eslint-disable-line no-useless-computed-key
            fontSize: 16,
       }
   },

    }));

export default useStyles;
