import { makeStyles } from '@material-ui/core/styles';
import { RiFacebookBoxLine } from 'react-icons/ri'

const useStyles = makeStyles((theme) => ({
    font: {
        flexGrow: 1,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        color: '#00000',
        fontFamily: 'liebedoni-solid',
        fontSize: 30,
    },
    text: {
        flexGrow: 1,
        display: 'flex',
        alignItems: 'center',
        color: '#000000',
        fontSize: 20,
        marginLeft: 5,
    }
    }));

export default useStyles;

export const Bars = styled(FaBars)`
display: none; 
color: #242326;

@media screen and (max-width: 768px) {
    display: flex; 
    position: absolute;
    top: 0;
    right 0; 
    transform: translate(-100%, 75%)
    font-size: 1.8rem; 
    cursor: pointer;
}
`
